(defn crawl-results-page [{:keys [result-page-html] :as m}] ;; ~ 20 ms
  (let [parsed-page (Jsoup/parse result-page-html)
        tr-orgs (-> (select-one parsed-page ".results_header") next-sibling
                  (.child 0) .children seq)]
    (map (fn [^Element tr-org]
           (let [title-a (select-one tr-org "td h3 a")
                 org-page-url (str website-domain (attr "href" title-a))
                 org-name (own-text title-a)
                 
                 ^Element category-p (select-one tr-org "p.category")
                 [location category cause] (->> category-p .textNodes seq ;; text nodes are the values of the fields
                                             (filter ;; removing white space text nodes
                                                (fn [^TextNode tn] (->> tn .text s/trim seq)))
                                             (map (fn [^TextNode tn] (.text tn))) ;; getting text
                                             (map (fn [text] ;; removing \s
                                                    ((re-matches #":\s(.*)" text) 1)))
                                             )
                 ]
             (-> m 
               (dissoc :result-page-html)
               (assoc :category category
                      :cause cause
                      :org_name org-name
                      :org_page_url org-page-url
                      :location location
                      ))
             ))
         tr-orgs))
  )

