(defn crawl-cn-org-page [{cn-html :org-cn-html :as org}]
  (let [parsed (Jsoup/parse cn-html) ;; parsing raw HTML to JSoup DOM
        title (-> parsed (select-one ".charityname") own-text) ;; the name of the organization
        tagline (-> parsed (select-one ".tagline") own-text)
        website_url (when-let [^Element ext-a (->> (select parsed "a") (filter #(= (own-text %) "Visit Web Site")) first)]
                      (.attr ext-a "href"))
        
        summary-boxes (select parsed ".summaryBox")
        mission-statement (-> (->> summary-boxes
                                 (filter #(when-let [a (select-one % "h2 a")] 
                                            (-> a own-text (= "Mission"))))
                                 first)
                             (select-one "p")
                             own-text)
        
        table-values (read-table-fields regular-fields-opts ;; fields that are not the income statement 
                                        (select parsed ".summaryBox .shadedtable td")) 
        
        is-elem (->> (select parsed ".summaryBox h2 a") ;; income statement element
                  (filter #(= (own-text %) "Income Statement"))
                  first
                  (ancestor-with-tag "div"))
        is-values (read-table-fields
                     income-statement-fields-opts
                     (set (concat (select is-elem "td a") (select is-elem "td strong"))))
        
        address-text (->> (find-having-text parsed "#leftnavcontent .rating strong" title)
                       (ancestor-with-tag "p") text (re-matches (re-pattern (str title " (.*)"))) second)
        
        ;[_ address & _] (re-matches
        ;                  (re-pattern (str title ;" (.*?)(tel: ([^a-z]*)\\s)??(fax: ([^a-z]*)\\s)??(TTY: ([^a-z]*)\\s)??EIN: (.*)"
        ;                                   ""
        ;                                   ))
        ;                  address-text)
        [_ address & _] (re-matches #"(.*?) (tel:|fax:|TTY:|EIN:).*" address-text)
        [_ tel] (re-find #"tel: ([\(\)\-0-9\s]{14})" address-text)
        [_ fax] (re-find #"fax: ([\(\)\-0-9\s]{14})" address-text)
        [_ tty] (re-find #"TTY: ([\(\)\-0-9\s]{14})" address-text)
        [_ ein] (re-find #"EIN: ([\d\-]+)" address-text)
        
        ret (-> org
              (dissoc :org-cn-html)
              (assoc :title title
                     :tagline tagline
                     :website_url website_url
                     :mission_statement mission-statement
                     
                     :address (s/trim address)
                     :tel tel :fax fax :tty tty
                     :ein ein
                     )) 
        
        ]
    (reduce (fn [m {:keys [field-name field-value]}] (assoc m field-name field-value))
            ret (concat table-values is-values))
    ))

